import java.util.Scanner;

public class Ring_rotate {
    public static Scanner scn=new Scanner(System.in);
    public static void Input(int A[][]){
        int n=A.length;
        int m=A[0].length;

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                A[i][j]=scn.nextInt();
            }
        }
    }
    public static void displayy(int A[][]){
        int n=A.length;
        int m=A[0].length;

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
               System.out.print( A[i][j]+" ");
            }
            System.out.println();
        }
    }
    public static void display(int A[]){
        int n=A.length;


        for(int i=0;i<n;i++){

                System.out.println(A[i]);

        }
    }

    public static void oneD(int A[][],int a[],int k,int s){
        int n=A.length;
        int m=A[0].length;
        int minr=(s-1);
        int minc=s-1;
        int maxr=n-1-(s-1);
        int maxc=m-1-(s-1);
        int count=0;
while(count<k) {
    for (int j = minc; j <= maxc; j++) {
        System.out.println("coutn="+count);
        System.out.println("minr="+minr +" "+"j="+j);
        a[count] = A[minr][j];

        count++;
    } minr++;

    for (int i = minr; i <= maxr; i++) {
        a[count] = A[i][maxc];

        count++;
    }maxc--;
    for (int j = maxc; j >= minc; j--) {
        a[count] = A[maxr][j];

        count++;
    }maxr--;
    for (int i = maxr; i >= minr; i--) {
        a[count] = A[i][minc];
        count++;
    }
}

 display(a);

    }
    public static void InsertoneD(int A[][],int a[],int k,int s){

        int n=A.length;
        int m=A[0].length;
        int minr=(s-1);
        int minc=s-1;
        int maxr=n-1-(s-1);
        int maxc=m-1-(s-1);
        int count=0;
        while(count<k) {
            for (int j = minc; j <= maxc; j++) {
                System.out.println("coutn="+count);
                System.out.println("minr="+minr +" "+"j="+j);
                A[minr][j]= a[count];

                count++;
            } minr++;

            for (int i = minr; i <= maxr; i++) {
               A[i][maxc]= a[count];

                count++;
            }maxc--;
            for (int j = maxc; j >= minc; j--) {
               A[maxr][j]= a[count];

                count++;
            }maxr--;
            for (int i = maxr; i >= minr; i--) {
                A[i][minc]= a[count];
                count++;
            }
        }

        display(a);

    }

    public static void Rotate(int A[],int i,int r){
     if (A.length<=r){
         r=r%A.length;
     }
      int j=r-1;
      while(i<=j){
          int k=A[i];
          A[i]=A[j];
          A[j]=k;
          i++;
          j--;
      }


        }

    public static void main(String []args){
        int n=scn.nextInt();
        int m=scn.nextInt();
        int A[][]=new int[n][m];
        Input(A);
        displayy(A);
        int small=(n<m?n:m);
        int k=0;
        int s=scn.nextInt();
        int r=scn.nextInt();
        if(  small/2 >=s ){
         k=(2*(n+m-( 2*s)))-4;
            System.out.print("K="+k);}
        else
        {
            int big=(n>m?n:m);

            k=big-(small-1);
            System.out.print("K="+k);
        }

     int a[]=new int[k];

        oneD(A,a,k,s);
        Rotate(a,0,r);
        Rotate(a,r,a.length-1);
        Rotate(a,0,a.length-1);
        InsertoneD(A, a,k, s);
        displayy(A);
    }                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
}
